"use client";

import { useStore } from "@nanostores/react";
import {
  getUiWalletAccountStorageKey,
  type UiWallet,
  type UiWalletAccount,
  uiWalletAccountBelongsToUiWallet,
  uiWalletAccountsAreSame,
  useWallets,
} from "@wallet-standard/react";
import {
  type ReactNode,
  useCallback,
  useEffect,
  useMemo,
  useState,
} from "react";

import {
  useAvailableWallets,
  useWalletAccount,
  useWalletNetwork,
} from "@/hooks/use-wallet";
import {
  WalletAccountContext,
  WalletNetworkContext,
  type WalletNetworkContextValue,
  WalletUiContext,
  type WalletUiContextProviderProps,
  type WalletUiContextValue,
} from "@/lib/chains/context";
import type { Network } from "@/lib/chains/network";
import {
  createStorageAccount,
  createStorageNetwork,
  type StorageAccount,
  type StorageNetwork,
} from "@/lib/chains/storage";

type WalletNetworkContextProviderProps = {
  children: ReactNode;
  networks: Network[];
  defaultNetworkId?: Network["id"];
  storage: StorageNetwork;
  onNetworkChange?: (networkId: Network["id"]) => void;
};

function WalletNetworkContextProvider({
  children,
  networks,
  defaultNetworkId,
  storage,
  onNetworkChange,
}: WalletNetworkContextProviderProps) {
  const storedNetworkId = useStore(storage.value);

  const networkFound = networks.find((n) => n.id === storedNetworkId);
  const firstNetwork = networks[0];
  const activeNetwork =
    networkFound ??
    networks.find((n) => n.id === defaultNetworkId) ??
    firstNetwork;

  if (!activeNetwork && networks.length > 0) {
    console.warn("No active network found and networks list is not empty.");
  }

  const value: WalletNetworkContextValue = {
    network: activeNetwork ?? { id: ":", label: "", url: "" },
    networks,
    setNetwork(networkId) {
      const network = networks.find((n) => n.id === networkId);
      if (!network) {
        throw new Error(`Network ${networkId} not found`);
      }
      storage.set(networkId);
      onNetworkChange?.(networkId);
    },
  };

  return (
    <WalletNetworkContext.Provider value={value}>
      {children}
    </WalletNetworkContext.Provider>
  );
}

let wasSetterInvoked = false;

function getSavedWalletAccount(
  wallets: readonly UiWallet[],
  savedWalletNameAndAddress?: string
): UiWalletAccount | undefined {
  if (wasSetterInvoked) {
    // After the user makes an explicit choice of wallet, stop trying to auto-select the
    // saved wallet, if and when it appears.
    return;
  }
  if (
    !savedWalletNameAndAddress ||
    typeof savedWalletNameAndAddress !== "string"
  ) {
    return;
  }
  const [savedWalletName, savedAccountAddress] =
    savedWalletNameAndAddress.split(":");
  if (!(savedWalletName && savedAccountAddress)) {
    return;
  }
  const wallet = wallets.find((w) => w.name === savedWalletName);
  if (!wallet) {
    return;
  }
  return wallet.accounts.find((a) => a.address === savedAccountAddress);
}

function findWalletAccount(
  account: UiWalletAccount,
  wallets: readonly UiWallet[]
): UiWalletAccount | undefined {
  for (const uiWallet of wallets) {
    for (const uiWalletAccount of uiWallet.accounts) {
      if (uiWalletAccountsAreSame(account, uiWalletAccount)) {
        return uiWalletAccount;
      }
    }
    if (
      uiWalletAccountBelongsToUiWallet(account, uiWallet) &&
      uiWallet.accounts[0]
    ) {
      return uiWallet.accounts[0];
    }
  }
  return;
}

function WalletAccountContextProvider({
  children,
  storage = createStorageAccount(),
}: {
  children: React.ReactNode;
  storage?: StorageAccount;
}) {
  const { network } = useWalletNetwork();
  const wallets = useWallets();
  const accountId = useStore(storage.value);
  const [account, setAccountInternal] = useState<UiWalletAccount | undefined>(
    () => getSavedWalletAccount(wallets, accountId)
  );

  const setAccount = useCallback(
    (setStateAction: React.SetStateAction<UiWalletAccount | undefined>) => {
      setAccountInternal((prevAccount) => {
        wasSetterInvoked = true;
        return typeof setStateAction === "function"
          ? setStateAction(prevAccount)
          : setStateAction;
      });
    },
    []
  );

  useEffect(() => {
    if (wasSetterInvoked) {
      const accountKey = account
        ? getUiWalletAccountStorageKey(account)
        : undefined;
      storage.set(accountKey ? accountKey : undefined);
    }
  }, [account, storage]);

  useEffect(() => {
    const savedWalletAccount = getSavedWalletAccount(wallets, accountId);
    if (savedWalletAccount && !account) {
      setAccountInternal(savedWalletAccount);
    }
  }, [accountId, wallets, account]);

  const walletAccount = useMemo(() => {
    if (!account) {
      return;
    }
    return findWalletAccount(account, wallets);
  }, [account, wallets]);

  useEffect(() => {
    if (account && !walletAccount && !wasSetterInvoked) {
      setTimeout(() => {
        setAccountInternal(undefined);
      }, 0);
    }
  }, [account, walletAccount]);

  const wallet = useMemo(() => {
    if (!walletAccount) {
      return;
    }
    for (const uiWallet of wallets) {
      for (const uiWalletAccount of uiWallet.accounts) {
        if (uiWalletAccountsAreSame(walletAccount, uiWalletAccount)) {
          return uiWallet;
        }
      }
      if (
        uiWalletAccountBelongsToUiWallet(walletAccount, uiWallet) &&
        uiWallet.accounts[0]
      ) {
        // If the selected account belongs to this connected wallet, at least, then
        // select one of its accounts.
        return uiWallet;
      }
    }
  }, [walletAccount, wallets]);

  const accountKeys = useMemo(() => {
    if (!account) {
      return [];
    }
    return [network.id, getUiWalletAccountStorageKey(account)].filter(Boolean);
  }, [account, network.id]);
  return (
    <WalletAccountContext.Provider
      value={useMemo(
        () => ({
          account: walletAccount,
          accountKeys,
          network,
          setAccount,
          wallet,
        }),
        [walletAccount, wallet, accountKeys, setAccount, network]
      )}
    >
      {children}
    </WalletAccountContext.Provider>
  );
}

function getChainNamespace(networkId: string | undefined): string | undefined {
  return networkId?.split(":")[0];
}

function getChainPrefix(namespace: string | undefined): string | undefined {
  return namespace ? `${namespace}:` : undefined;
}

function isClipboardAvailable(): boolean {
  return (
    typeof globalThis !== "undefined" &&
    globalThis.navigator?.clipboard?.writeText !== undefined
  );
}

async function copyToClipboard(text: string): Promise<void> {
  if (!isClipboardAvailable()) {
    return;
  }
  await globalThis.navigator.clipboard.writeText(text);
}

function WalletUiContextProvider({
  children,
  client,
}: WalletUiContextProviderProps) {
  const { account, accountKeys, setAccount, wallet } = useWalletAccount();
  const { network } = useWalletNetwork();

  const chainNamespace = getChainNamespace(network?.id);
  const chainPrefix = getChainPrefix(chainNamespace);

  const wallets = useAvailableWallets(chainPrefix);
  const connected = Boolean(wallet?.accounts && wallet.accounts.length > 0);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const connect = useCallback(
    (walletAccount: UiWalletAccount) => {
      setAccount(walletAccount);
      setIsModalOpen(false);
    },
    [setAccount]
  );

  const disconnect = useCallback(() => {
    setAccount(undefined);
  }, [setAccount]);

  const copy = useCallback(async () => {
    const address = account?.address;
    if (!address) {
      return;
    }
    await copyToClipboard(address);
  }, [account]);

  const value = useMemo<WalletUiContextValue>(
    () => ({
      account,
      accountKeys,
      client,
      connect,
      connected,
      copy,
      disconnect,
      isModalOpen,
      network,
      setIsModalOpen,
      wallet,
      wallets,
    }),
    [
      account,
      accountKeys,
      client,
      connect,
      connected,
      copy,
      disconnect,
      isModalOpen,
      network,
      wallet,
      wallets,
    ]
  );

  return (
    <WalletUiContext.Provider value={value}>
      {children}
    </WalletUiContext.Provider>
  );
}

interface WalletConfig extends Omit<WalletUiContextProviderProps, "children"> {
  accountStorage?: StorageAccount;
  networkStorage?: StorageNetwork;
  networks: Network[];
  defaultNetworkId?: Network["id"];
  onNetworkChange?: (networkId: Network["id"]) => void;
}

function createWalletConfig(props: WalletConfig): WalletConfig {
  return { ...props };
}

function WalletProvider({
  children,
  config: {
    accountStorage,
    networkStorage,
    networks,
    defaultNetworkId,
    onNetworkChange,
    ...uiConfig
  },
}: {
  children: ReactNode;
  config: WalletConfig;
}) {
  return (
    <WalletNetworkContextProvider
      defaultNetworkId={defaultNetworkId}
      networks={networks}
      onNetworkChange={onNetworkChange}
      storage={networkStorage ?? createStorageNetwork()}
    >
      <WalletAccountContextProvider
        storage={accountStorage ?? createStorageAccount()}
      >
        <WalletUiContextProvider {...uiConfig}>
          {children}
        </WalletUiContextProvider>
      </WalletAccountContextProvider>
    </WalletNetworkContextProvider>
  );
}

export { WalletProvider, createWalletConfig };

"use client";

import {
  AlertCircle,
  ChevronLeftIcon,
  InfoIcon,
  LogOutIcon,
} from "lucide-react";
import type React from "react";
import { useEffect, useState } from "react";
import {
  useWallet,
  useWalletAccount,
} from "@/hooks/use-wallet";
import { ellipsify } from "@/lib/ellipsify";
import { Button } from "@/registry/default/ui/button";
import {
  ButtonGroup,
  ButtonGroupText,
} from "@/registry/default/ui/button-group";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/registry/default/ui/dialog";
import {
  Item,
  ItemActions,
  ItemContent,
  ItemDescription,
  ItemMedia,
  ItemTitle,
} from "@/registry/default/ui/item";
import {
  NO_ERROR,
  WalletErrorDialog,
} from "@/components/wallet/error";
import { WalletIcon } from "@/components/wallet/icon";
import { WalletList } from "@/components/wallet/list";
import { WalletOnboarding } from "@/components/wallet/onboarding";

function ConnectWallet({
  className,
  children,
  variant = "default",
  ...props
}: React.ComponentProps<typeof Button>) {
  const { wallets, disconnect } = useWallet();
  const { setAccount, account, wallet } = useWalletAccount();
  const [open, setOpen] = useState(false);
  const [showInfo, setShowInfo] = useState(false);
  const [error, setError] = useState<symbol>(NO_ERROR);

  useEffect(() => {
    if (!open) {
      setShowInfo(false);
    }
  }, [open]);

  if (account) {
    return (
      <ButtonGroup className={className}>
        <ButtonGroupText>
          <WalletIcon className="mr-2 size-4 rounded-sm" wallet={wallet} />
          {ellipsify(account.address)}
        </ButtonGroupText>
        <Button
          onClick={() => disconnect()}
          size="icon"
          variant={variant}
          {...props}
        >
          <LogOutIcon />
        </Button>
      </ButtonGroup>
    );
  }

  return (
    <>
      <Dialog onOpenChange={setOpen} open={open}>
        <DialogTrigger asChild>
          <Button className={className} variant={variant} {...props}>
            {children || "Connect Wallet"}
          </Button>
        </DialogTrigger>
        <DialogContent showCloseButton={false}>
          <DialogHeader className="flex flex-row items-center justify-between">
            {!showInfo && (
              <DialogTitle className="">Connect Wallet</DialogTitle>
            )}
            <DialogDescription className="sr-only" />
            <Button
              className="size-7 rounded-full"
              onClick={() => setShowInfo(!showInfo)}
              size="icon"
              variant="ghost"
            >
              {showInfo ? (
                <ChevronLeftIcon className="text-foreground/50 group-hover:text-foreground" />
              ) : (
                <InfoIcon className="text-foreground/50 group-hover:text-foreground" />
              )}
            </Button>
          </DialogHeader>

          {showInfo ? (
            <WalletOnboarding onClose={() => setShowInfo(false)} />
          ) : (
            <WalletList
              select={(selectedAccount) => {
                setAccount(selectedAccount);
                setOpen(false);
              }}
              wallets={wallets}
            />
          )}
        </DialogContent>
      </Dialog>
      {error !== NO_ERROR && (
        <WalletErrorDialog error={error} onClose={() => setError(NO_ERROR)} />
      )}
    </>
  );
}
interface ConnectWalletPromptProps extends React.ComponentProps<typeof Item> {
  title?: string;
  description?: string;
}
function ConnectWalletPrompt({
  variant = "outline",
  title = "Wallet not connected",
  description = "Please connect your wallet to continue.",
  ...props
}: ConnectWalletPromptProps) {
  const { account } = useWalletAccount();
  if (account) {
    return null;
  }
  return (
    <Item variant={variant} {...props}>
      <ItemMedia variant="icon">
        <AlertCircle className="size-4 text-destructive" />
      </ItemMedia>
      <ItemContent>
        <ItemTitle>{title}</ItemTitle>
        <ItemDescription className="-mt-1 text-xs">
          {description}
        </ItemDescription>
      </ItemContent>
      <ItemActions>
        <ConnectWallet />
      </ItemActions>
    </Item>
  );
}

export { ConnectWallet, ConnectWalletPrompt };

import type { UiWallet, UiWalletAccount } from "@wallet-standard/react";
import { CircleAlertIcon, ExternalLink } from "lucide-react";
import type React from "react";
import {
  useWalletAccount,
  useWalletConnection,
} from "@/hooks/use-wallet";
import { cn } from "@/lib/utils";
import { Alert, AlertDescription } from "@/registry/default/ui/alert";
import { Badge } from "@/registry/default/ui/badge";
import { Button } from "@/registry/default/ui/button";
import { Spinner } from "@/registry/default/ui/spinner";
import { WalletIcon } from "@/components/wallet/icon";

function NoWalletDetected({ className }: { className?: string }) {
  return (
    <div className={cn("space-y-3", className)}>
      <Alert variant="destructive">
        <CircleAlertIcon className="mt-0.5" />
        <AlertDescription>
          No wallet detected in this browser. Please install a Solana wallet
          extension.
        </AlertDescription>
      </Alert>
      <Button
        className="w-full"
        onClick={() => window.open("https://phantom.app/download", "_blank")}
      >
        Download Phantom Wallet
        <ExternalLink />
      </Button>
    </div>
  );
}

interface WalletListItemProps extends React.ComponentProps<typeof Button> {
  select?: (wallet: UiWalletAccount) => Promise<void> | void;
  wallet: UiWallet;
}

function WalletListItem({
  wallet,
  select,
  className,
  ...props
}: WalletListItemProps) {
  const { connect, isConnecting } = useWalletConnection({ wallet });
  const { wallet: connectedWallet } = useWalletAccount();
  const isConnected = connectedWallet?.name === wallet.name;

  return (
    <Button
      className={cn("h-auto", className)}
      disabled={isConnecting || isConnected}
      onClick={async () => {
        const accounts = await connect();
        const account = accounts?.[0];
        if (account && select) {
          await select(account);
        }
      }}
      {...props}
    >
      <WalletIcon wallet={wallet} />
      <span className="grow text-left">{wallet.name}</span>
      {isConnecting && <Spinner />}
      {isConnected && !isConnecting && (
        <Badge className="ml-2" variant="secondary">
          Connected
        </Badge>
      )}
    </Button>
  );
}

interface WalletListProps extends React.ComponentProps<"div"> {
  select?: (account: UiWalletAccount) => Promise<void> | void;
  wallets: UiWallet[];
}

function WalletList({ select, wallets, ...props }: WalletListProps) {
  if (wallets.length === 0) {
    return <NoWalletDetected className={props.className} />;
  }

  return (
    <div
      className={cn(
        "flex max-h-[300px] flex-col space-y-2 overflow-y-auto p-2",
        props.className
      )}
      {...props}
    >
      {wallets.map((wallet, index) => (
        <WalletListItem
          key={`${wallet.name}-${index}`}
          select={select}
          wallet={wallet}
        />
      ))}
    </div>
  );
}

export { WalletList };

"use client";

import type { UiWallet, UiWalletAccount } from "@wallet-standard/react";
import { CopyIcon, LogOutIcon } from "lucide-react";
import type React from "react";
import { useMemo, useState } from "react";
import { toast } from "sonner";
import {
  useWallet,
  useWalletAccount,
  useWalletConnection,
} from "@/hooks/use-wallet";
import { ellipsify } from "@/lib/ellipsify";
import { Button } from "@/registry/default/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/registry/default/ui/dropdown-menu";
import { ConnectWallet } from "@/components/wallet/connect";
import {
  NO_ERROR,
  WalletErrorDialog,
} from "@/components/wallet/error";
import { WalletIcon } from "@/components/wallet/icon";

function WalletMenuContent({
  account,
  selectedWallet,
  onDisconnect,
  onError,
  setDropdownOpen,
}: {
  account: UiWalletAccount;
  selectedWallet: UiWallet;
  onDisconnect: () => void;
  onError: (error: unknown) => void;
  setDropdownOpen: (open: boolean) => void;
}) {
  const { copy } = useWallet();
  const { disconnect, isDisconnecting } = useWalletConnection({
    wallet: selectedWallet,
  });

  const handleCopy = () => {
    try {
      copy();
      toast.success("Address copied to clipboard");
    } catch {
      toast.error("Failed to copy address");
    }
  };

  const handleDisconnect = async () => {
    try {
      await disconnect();
      onDisconnect();
      setDropdownOpen(false);
    } catch (err) {
      onError(err);
    }
  };

  return (
    <>
      <DropdownMenuLabel className="flex items-center gap-2">
        <WalletIcon className="rounded-sm" wallet={selectedWallet} />
        <div className="flex flex-col">
          <span>{selectedWallet.name}</span>
          <span className="text-muted-foreground text-xs">Connected</span>
        </div>
      </DropdownMenuLabel>
      <DropdownMenuSeparator />
      <DropdownMenuItem onClick={handleCopy}>
        <CopyIcon />
        <span className="font-mono">{ellipsify(account.address, 8)}</span>
      </DropdownMenuItem>
      <DropdownMenuSeparator />
      <DropdownMenuItem
        disabled={isDisconnecting}
        onClick={handleDisconnect}
        variant="destructive"
      >
        <LogOutIcon />
        <span>{isDisconnecting ? "Disconnecting..." : "Disconnect"}</span>
      </DropdownMenuItem>
    </>
  );
}

function WalletMenu({
  className,
  children,
  variant = "default",
  ...props
}: React.ComponentProps<typeof Button>) {
  const { wallets } = useWallet();
  const { account, setAccount } = useWalletAccount();
  const [error, setError] = useState<symbol>(NO_ERROR);
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const selectedWallet = useMemo(() => {
    if (!account) {
      return;
    }
    return wallets.find((wallet) =>
      wallet.accounts.some((acc) => acc.address === account.address)
    );
  }, [account, wallets]);

  const handleError = (err: unknown) => {
    setError(err as symbol);
  };

  if (!account) {
    return <ConnectWallet className={className} variant={variant} {...props} />;
  }

  if (!selectedWallet) {
    return null;
  }

  return (
    <>
      <DropdownMenu onOpenChange={setDropdownOpen} open={dropdownOpen}>
        <DropdownMenuTrigger asChild>
          <Button className={className} variant={variant} {...props}>
            <WalletIcon className="size-4 rounded-sm" wallet={selectedWallet} />
            <span className="font-mono text-sm">
              {ellipsify(account.address, 4)}
            </span>
          </Button>
        </DropdownMenuTrigger>
        <DropdownMenuContent>
          <WalletMenuContent
            account={account}
            onDisconnect={() => setAccount(undefined)}
            onError={handleError}
            selectedWallet={selectedWallet}
            setDropdownOpen={setDropdownOpen}
          />
        </DropdownMenuContent>
      </DropdownMenu>
      {error !== NO_ERROR && (
        <WalletErrorDialog error={error} onClose={() => setError(NO_ERROR)} />
      )}
    </>
  );
}

export { WalletMenu };

"use client";

import { useEffect, useId, useState } from "react";
import { useWalletNetwork } from "@/hooks/use-wallet";
import type { Network } from "@/lib/chains/network";
import { cn } from "@/lib/utils";
import { Badge } from "@/registry/default/ui/badge";
import {
  NativeSelect,
  NativeSelectOption,
} from "@/registry/default/ui/native-select";

interface NetworkBadgeProps extends React.ComponentProps<typeof Badge> {
  network?: Network;
}

function NetworkBadge({
  className,
  network: propsNetwork,
  variant = "default",
  ...props
}: NetworkBadgeProps) {
  const { network: contextNetwork } = useWalletNetwork();
  const [mountedNetwork, setMountedNetwork] = useState<Network | undefined>(
    () => propsNetwork || contextNetwork
  );

  useEffect(() => {
    setMountedNetwork(propsNetwork || contextNetwork);
  }, [propsNetwork, contextNetwork]);

  const network = mountedNetwork;

  if (!network) {
    return null;
  }

  const networkName = network.label;

  return (
    <Badge
      className={cn("gap-1.5", className)}
      suppressHydrationWarning
      variant={variant}
      {...props}
    >
      {networkName}
    </Badge>
  );
}

interface NetworkSelectProps
  extends React.ComponentProps<typeof NativeSelect> {}

function NetworkSelect({ className, id, ...props }: NetworkSelectProps) {
  const generatedId = useId();
  const { networks, network, setNetwork } = useWalletNetwork();

  return (
    <NativeSelect
      className={cn("w-full", className)}
      id={id ?? generatedId}
      onChange={(e) => setNetwork(e.target.value as Network["id"])}
      value={network.id}
      {...props}
    >
      {networks.map((item) => (
        <NativeSelectOption key={item.id} value={item.id}>
          {item.label}
        </NativeSelectOption>
      ))}
    </NativeSelect>
  );
}

export { NetworkSelect, NetworkBadge };

"use client";
import {
  isWalletStandardError,
  WALLET_STANDARD_ERROR__FEATURES__WALLET_ACCOUNT_CHAIN_UNSUPPORTED,
  WALLET_STANDARD_ERROR__FEATURES__WALLET_ACCOUNT_FEATURE_UNIMPLEMENTED,
  WALLET_STANDARD_ERROR__FEATURES__WALLET_FEATURE_UNIMPLEMENTED,
} from "@wallet-standard/core";

import React from "react";
import {
  AlertDialog,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/registry/default/ui/alert-dialog";
import { Button } from "@/registry/default/ui/button";

const NO_ERROR = Symbol("NO_ERROR");

function getWalletErrorMessage(
  err: unknown,
  fallbackMessage: React.ReactNode
): React.ReactNode {
  if (
    isWalletStandardError(
      err,
      WALLET_STANDARD_ERROR__FEATURES__WALLET_ACCOUNT_FEATURE_UNIMPLEMENTED
    )
  ) {
    return (
      <>
        This account does not support the{" "}
        <code className="rounded bg-card px-1.5 py-0.5 font-mono text-sm">
          {err.context.featureName}
        </code>{" "}
        feature
      </>
    );
  }
  if (
    isWalletStandardError(
      err,
      WALLET_STANDARD_ERROR__FEATURES__WALLET_FEATURE_UNIMPLEMENTED
    )
  ) {
    return (
      <div className="flex flex-col gap-4">
        <p>
          The wallet {err.context.walletName} (
          {err.context.supportedChains.sort().map((chain, ii, { length }) => (
            <React.Fragment key={chain}>
              <code className="rounded bg-card px-1.5 py-0.5 font-mono text-sm">
                {chain}
              </code>
              {ii === length - 1 ? null : ", "}
            </React.Fragment>
          ))}
          ) does not support the
          <code className="rounded bg-card px-1.5 py-0.5 font-mono text-sm">
            {err.context.featureName}
          </code>
          feature.
        </p>
        <div>
          Features supported:
          <ul className="mt-2 list-disc pl-5">
            {err.context.supportedFeatures.sort().map((featureName) => (
              <li key={featureName}>
                <code className="rounded bg-card px-1.5 py-0.5 font-mono text-sm">
                  {featureName}
                </code>
              </li>
            ))}
          </ul>
        </div>
      </div>
    );
  }
  if (
    isWalletStandardError(
      err,
      WALLET_STANDARD_ERROR__FEATURES__WALLET_ACCOUNT_CHAIN_UNSUPPORTED
    )
  ) {
    return (
      <div className="flex flex-col gap-4">
        <p>
          This account does not support the chain{" "}
          <code className="rounded bg-card px-1.5 py-0.5 font-mono text-sm">
            {err.context.chain}
          </code>
          .
        </p>
        <div>
          Chains supported:
          <ul className="mt-2 list-disc pl-5">
            {err.context.supportedChains.sort().map((chain) => (
              <li key={chain}>
                <code className="rounded bg-card px-1.5 py-0.5 font-mono text-sm">
                  {chain}
                </code>
              </li>
            ))}
          </ul>
        </div>
      </div>
    );
  }
  if (err && typeof err === "object" && "message" in err) {
    return String(err.message);
  }
  return fallbackMessage;
}

type WalletErrorDialogProps = {
