type FormatSolArgs = {
  balance: bigint;
  locale?: Intl.LocalesArgument;
  options?: Intl.NumberFormatOptions;
};

function formatSol({
  balance,
  locale = "en-US",
  options = {
    style: "decimal",
    minimumFractionDigits: 2,
    maximumFractionDigits: 5,
  },
}: FormatSolArgs) {
  if (!balance) {
    return "0";
  }
  return Intl.NumberFormat(locale, {
